import React, { useState } from 'react';
import './TopicPanel.css';
import { FaStar, FaRegStar } from 'react-icons/fa';

const TopicPanel = () => {
  const [favorites, setFavorites] = useState(new Set());

  const categoryColors = {
    camera: '#FFB5B5',    // 파스텔 핑크
    control: '#B5D8FF',   // 파스텔 블루
    sensor: '#C8E6C9',    // 파스텔 그린
    transform: '#FFE0B2'  // 파스텔 오렌지
  };

  const topics = [
    { name: '/camera/image_raw', category: 'camera' },
    { name: '/cmd_vel', category: 'control' },
    { name: '/odom', category: 'sensor' },
    { name: '/tf', category: 'transform' },
    { name: '/scan', category: 'sensor' }
  ];

  const toggleFavorite = (topicName) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(topicName)) {
      newFavorites.delete(topicName);
    } else {
      newFavorites.add(topicName);
    }
    setFavorites(newFavorites);
  };

  return (
    <div className="topic-container">
      <div className="topic-panel">
        <h2 className="panel-title">Topic</h2>
        <div className="topic-list">
          {topics.map((topic, index) => (
            <div key={index} className="topic-item">
              <button 
                className="favorite-button"
                onClick={() => toggleFavorite(topic.name)}
              >
                {favorites.has(topic.name) ? <FaStar className="star-icon filled" /> : <FaRegStar className="star-icon" />}
              </button>
              <div 
                className="category-tag"
                style={{ 
                  backgroundColor: categoryColors[topic.category],
                  color: '#333'
                }}
              >
                {topic.category}
              </div>
              <span className="topic-name">{topic.name}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TopicPanel;
