import React, { useState, useEffect } from 'react';
import './BatteryPanel.css';
import { FaBatteryThreeQuarters } from 'react-icons/fa';
import { useWebSocket } from '../contexts/WebSocketContext';

const BatteryPanel = () => {
  const [batteryPercentage, setBatteryPercentage] = useState('--');
  const { ws, connectedCustomer } = useWebSocket();

  useEffect(() => {
    if (!ws || !connectedCustomer) return;

    const handleMessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.topic === '/battery_state') {
        setBatteryPercentage(`${Math.round(data.msg.percentage)}%`);
      }
    };

    ws.addEventListener('message', handleMessage);

    return () => {
      ws.removeEventListener('message', handleMessage);
    };
  }, [ws, connectedCustomer]);

  // 고객이 연결되어 있지 않으면 null을 반환하여 컴포넌트를 렌더링하지 않음
  if (!connectedCustomer) {
    return null;
  }

  return (
    <div className="battery-container">
      <div className="battery-panel">
        <div className="battery-content">
          <div className="battery-info">
            <span className="battery-label">Battery</span>
            <div className="battery-percentage">{batteryPercentage}</div>
          </div>
          <div className="battery-icon">
            <FaBatteryThreeQuarters />
          </div>
        </div>
      </div>
    </div>
  );
};

export default BatteryPanel;
