import React, { useState } from "react";
import axios from 'axios';
import './Report.css';

const API_BASE_URL = 'http://211.188.63.134:80';

export default function ReportModal({ isOpen, onClose, data, onUpdate }) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    receipt_number: data?.receptionId || "",
    reporter_name: data?.reporterName || "",
    handler_name: data?.handlerName || "",
    car_model: data?.reporterModel || "",
    received_time: data?.receptionDateTime || "",
    action_content: data?.actionDetails || ""
  });

  if (!isOpen) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validateForm = () => {
    if (!formData.receipt_number) return "접수번호를 입력해주세요.";
    if (!formData.reporter_name) return "신고자명을 입력해주세요.";
    if (!formData.handler_name) return "담당자명을 입력해주세요.";
    if (!formData.car_model) return "차량 모델을 입력해주세요.";
    if (!formData.received_time) return "접수시간을 입력해주세요.";
    if (!formData.action_content) return "조치내용을 입력해주세요.";
    return null;
  };

  const handleDelete = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');

      await axios.delete(`${API_BASE_URL}/reports/delete/${data.id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      onUpdate();
      onClose();
    } catch (err) {
      console.error('보고서 삭제 실패:', err);
      if (err.response?.status === 401) {
        window.location.reload();
      } else {
        setError(err.response?.data?.detail || '보고서 삭제 중 오류가 발생했습니다.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // 폼 유효성 검사
    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');

      // 날짜 형식 변환
      const formattedData = {
        ...formData,
        received_time: new Date(formData.received_time).toISOString()
      };

      const response = await axios.post(`${API_BASE_URL}/reports/create`, formattedData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data) {
        onUpdate();
        onClose();
      } else {
        throw new Error('서버 응답이 올바르지 않습니다.');
      }
    } catch (err) {
      console.error('보고서 저장 실패:', err);
      setLoading(false);
      
      if (err.response?.status === 401) {
        window.location.reload();
      } else if (err.response?.data?.detail) {
        setError(err.response.data.detail);
      } else if (err.response?.status === 422) {
        setError('입력하신 데이터가 올바르지 않습니다. 모든 필드를 확인해주세요.');
      } else {
        setError('보고서 저장 중 오류가 발생했습니다. 잠시 후 다시 시도해주세요.');
      }
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Report</h2>
          <button onClick={onClose} className="close-button">×</button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form className="report-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="receipt_number">접수번호</label>
            <input
              type="text"
              id="receipt_number"
              name="receipt_number"
              value={formData.receipt_number}
              onChange={handleChange}
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="reporter_name">신고자명</label>
            <input
              type="text"
              id="reporter_name"
              name="reporter_name"
              value={formData.reporter_name}
              onChange={handleChange}
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="handler_name">담당자명</label>
            <input
              type="text"
              id="handler_name"
              name="handler_name"
              value={formData.handler_name}
              onChange={handleChange}
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="car_model">차량 모델</label>
            <input
              type="text"
              id="car_model"
              name="car_model"
              value={formData.car_model}
              onChange={handleChange}
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="received_time">접수시간</label>
            <input
              type="datetime-local"
              id="received_time"
              name="received_time"
              value={formData.received_time}
              onChange={handleChange}
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="action_content">조치내용</label>
            <textarea
              id="action_content"
              name="action_content"
              rows="4"
              value={formData.action_content}
              onChange={handleChange}
              disabled={loading}
              required
            />
          </div>

          <div className="form-actions">
            {data?.id && (
              <button 
                type="button" 
                onClick={handleDelete} 
                className="delete-button"
                disabled={loading}
              >
                삭제
              </button>
            )}
            <button 
              type="submit"
              disabled={loading}
            >
              {loading ? '처리 중...' : '저장'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
