import React, { useState } from "react";
import axios from 'axios';
import './Report.css';

const API_BASE_URL = 'http://211.188.63.134:80';

export default function ReportModal({ isOpen, onClose, data, onUpdate }) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    receipt_number: data?.receptionId || "",
    reporter_name: data?.reporterName || "",
    handler_name: data?.handlerName || "",
    car_model: data?.reporterModel || "",
    received_time: data?.receptionDateTime || "",
    action_content: data?.actionDetails || ""
  });

  if (!isOpen) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleDelete = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      
      if (!token) {
        setError('로그인이 필요합니다');
        return;
      }

      await axios.delete(`${API_BASE_URL}/reports/delete/${data.id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      onUpdate(); // 목록 새로고침
      onClose();
    } catch (err) {
      console.error('보고서 삭제 실패:', err);
      setError(err.response?.data?.detail || '보고서 삭제에 실패했습니다');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      
      if (!token) {
        setError('로그인이 필요합니다');
        return;
      }

      await axios.post(`${API_BASE_URL}/reports/create`, formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      onUpdate(); // 목록 새로고침
      onClose();
    } catch (err) {
      console.error('보고서 저장 실패:', err);
      setError(err.response?.data?.detail || '보고서 저장에 실패했습니다');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Report</h2>
          <button onClick={onClose} className="close-button">×</button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form className="report-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="receipt_number">접수번호</label>
            <input
              type="text"
              id="receipt_number"
              name="receipt_number"
              value={formData.receipt_number}
              onChange={handleChange}
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="reporter_name">신고자명</label>
            <input
              type="text"
              id="reporter_name"
              name="reporter_name"
              value={formData.reporter_name}
              onChange={handleChange}
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="handler_name">담당자명</label>
            <input
              type="text"
              id="handler_name"
              name="handler_name"
              value={formData.handler_name}
              onChange={handleChange}
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="car_model">차량 모델</label>
            <input
              type="text"
              id="car_model"
              name="car_model"
              value={formData.car_model}
              onChange={handleChange}
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="received_time">접수시간</label>
            <input
              type="datetime-local"
              id="received_time"
              name="received_time"
              value={formData.received_time}
              onChange={handleChange}
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="action_content">조치내용</label>
            <textarea
              id="action_content"
              name="action_content"
              rows="4"
              value={formData.action_content}
              onChange={handleChange}
              disabled={loading}
            />
          </div>

          <div className="form-actions">
            {data?.id && (
              <button 
                type="button" 
                onClick={handleDelete} 
                className="delete-button"
                disabled={loading}
              >
                삭제
              </button>
            )}
            <button 
              type="submit"
              disabled={loading}
            >
              {loading ? '처리 중...' : '저장'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
