import { useState, useEffect } from "react";
import "./LoginModal.css";
import logo from '../assets/images/logo.png';
import axios from 'axios';

function LoginModal({ onClose, onLogin, onSwitchToRegister }) {
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async () => {
    if (!id || !pw) {
      setError("아이디와 비밀번호를 모두 입력해주세요.");
      return;
    }

    setIsLoading(true);
    setError("");
    
    try {
      const formData = new URLSearchParams();
      formData.append('email', id);
      formData.append('password', pw);

      console.log('로그인 시도:', { email: id });

      const response = await axios.post('http://211.188.63.134:8000/login', formData, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Accept': 'application/json',
          'Access-Control-Allow-Origin': '*'
        },
        timeout: 10000,
        withCredentials: false
      });

      console.log('Login response:', response.data);

      if (response.data.access_token) {
        localStorage.setItem('token', response.data.access_token);
        localStorage.setItem('token_type', response.data.token_type);
        localStorage.setItem('user', id);
        
        onLogin(id);
        onClose();
      } else {
        setError(response.data.message || '로그인에 실패했습니다.');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.code === 'ECONNABORTED') {
        setError('서버 응답 시간이 초과되었습니다. 다시 시도해주세요.');
      } else if (err.response) {
        console.error('Server error response:', err.response);
        setError(err.response.data?.message || err.response.data?.detail || '로그인에 실패했습니다.');
      } else if (err.request) {
        console.error('No response received:', err.request);
        setError('서버에 연결할 수 없습니다. 서버가 실행 중인지 확인해주세요.');
      } else {
        console.error('Request setup error:', err.message);
        setError('로그인 요청 중 오류가 발생했습니다.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');
    if (token && user) {
      onLogin(user);
      onClose();
    }
  }, [onLogin, onClose]);

  return (
    <div className="login-modal-overlay">
      <div className="login-modal">
        <img src={logo} alt="Logo" className="login-logo" />
        <h2 className="login-title">로그인</h2>
        {error && <div className="error-message">{error}</div>}

        <input
          type="email"
          placeholder="이메일"
          value={id}
          onChange={(e) => setId(e.target.value)}
          className="login-input"
          disabled={isLoading}
        />

        <input
          type="password"
          placeholder="비밀번호"
          value={pw}
          onChange={(e) => setPw(e.target.value)}
          className="login-input"
          disabled={isLoading}
        />

        <button 
          className="login-button" 
          onClick={handleLogin}
          disabled={isLoading}
        >
          {isLoading ? '로그인 중...' : '로그인'}
        </button>

        <p
          className="signup-link"
          onClick={onSwitchToRegister}
          style={{ pointerEvents: isLoading ? 'none' : 'auto' }}
        >
          회원가입
        </p>
      </div>
    </div>
  );
}

export default LoginModal;
