import React, { useEffect, useState } from 'react';
import { useROS } from '../contexts/ROSContext';
import './SpeedPanel.css';
import { FaTachometerAlt } from 'react-icons/fa';

const SpeedPanel = () => {
  const [speed, setSpeed] = useState(0.0);
  const { createTopic, connectionStatus } = useROS();

  useEffect(() => {
    if (connectionStatus !== 'connected') {
      setSpeed(0.0);
      return;
    }

    const cmdVelTopic = createTopic('/cmd_vel', 'geometry_msgs/TwistStamped');

    if (cmdVelTopic) {
      cmdVelTopic.subscribe((message) => {
        const linearSpeed = Math.abs(message.twist.linear.x);
        const angularSpeed = Math.abs(message.twist.angular.z);
        const currentSpeed = Math.max(linearSpeed, angularSpeed);
        setSpeed(currentSpeed.toFixed(2));
      });

      return () => {
        cmdVelTopic.unsubscribe();
      };
    }
  }, [connectionStatus, createTopic]);

  return (
    <div className="speed-container">
      <div className="speed-panel">
        <div className="speed-content">
          <div className="speed-info">
            <span className="speed-label">Speed</span>
            <div className="speed-value">{speed} m/s</div>
          </div>
          <div className="speed-icon">
            <FaTachometerAlt />
          </div>
        </div>
      </div>
    </div>
  );
};

export default SpeedPanel;
