import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ReportsPanel.css';
import Report from './Report';  // Report 컴포넌트 import

const API_BASE_URL = 'http://211.188.63.134:80';

/** 더미 데이터: props로 안 넘겨주면 이걸 씁니다
const dummyReports = [
  { title: '주차 문제',       date: '2025-05-10', status: 'done' },
  { title: '시동 꺼짐',       date: '2025-05-01', status: 'done'    },
  { title: '기름 없음',       date: '2025-04-01', status: 'done' },
  { title: '다른 차량과 충돌', date: '2025-03-25', status: 'done'    },
  { title: '경로 탐색 문제', date: '2025-03-15', status: 'done' },
  { title: '몰라',         date: '2025-01-30', status: 'done'    },
  { title: '허허허',       date: '2024-12-20', status: 'done' },
];
*/
const dummyReports = [
  {
    title: "주차 문제",
    date: "2025-05-10",
    receptionId: "001",
    reporterName: "일등이",
    handlerName: "김가천",
    reporterModel: "현대 아반떼",
    receptionDateTime: "2025-05-10T10:00",
    actionDetails: "차단봉 설치 예정",
    status: "done"
  },
  {
    title: "시동 꺼짐",
    date: "2025-05-01",
    receptionId: "002",
    reporterName: "이등이",
    handlerName: "서무당",
    reporterModel: "기아 K5",
    receptionDateTime: "2025-05-01T09:30",
    actionDetails: "정비소 연결 완료",
    status: "done"
  },
  {
    title: "기름 없음",
    date: "2025-04-01",
    receptionId: "003",
    reporterName: "삼등이",
    handlerName: "최무한",
    reporterModel: "현대 벨로스터N",
    receptionDateTime: "2025-04-01T14:15",
    actionDetails: "즉시 주유 요청",
    status: "done"
  }
];


/**
 * ReportsPanel
 * @param {Array<{ title: string, date: string, status?: string }>} reports
 */
export default function ReportsPanel({ onSelectReport }) {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isReportModalOpen, setReportModalOpen] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);

  const fetchReports = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');

      // id 목록을 가져옵니다
      const listsResponse = await axios.get(`${API_BASE_URL}/reports/lists`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      console.log('리포트 ID 목록:', listsResponse.data);

      if (!Array.isArray(listsResponse.data)) {
        throw new Error('서버에서 올바른 형식의 데이터를 받지 못했습니다.');
      }

      // 각 ID에 대한 상세 정보를 가져옵니다
      const reportsPromises = listsResponse.data.map(async (id) => {
        try {
          const reportResponse = await axios.get(`${API_BASE_URL}/reports/${id}`, {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });

          console.log(`리포트 ID ${id}의 상세정보:`, reportResponse.data);

          const receivedTime = new Date(reportResponse.data.received_time);
          
          return {
            id: id,
            receipt_number: reportResponse.data.receipt_number,
            title: `${reportResponse.data.reporter_name} - ${reportResponse.data.car_model}`,
            date: receivedTime.toLocaleString('ko-KR', {
              year: 'numeric',
              month: '2-digit',
              day: '2-digit',
              hour: '2-digit',
              minute: '2-digit',
              hour12: false
            }),
            reporterName: reportResponse.data.reporter_name,
            handlerName: reportResponse.data.handler_name,
            reporterModel: reportResponse.data.car_model,
            receptionDateTime: reportResponse.data.received_time,
            actionDetails: reportResponse.data.action_content,
            status: 'done'
          };
        } catch (err) {
          console.error(`리포트 ID ${id} 조회 실패:`, err);
          return null;
        }
      });

      const reportDetails = (await Promise.all(reportsPromises))
        .filter(report => report !== null);
      
      // 날짜 기준으로 내림차순 정렬 (최신순)
      const sortedReports = reportDetails.sort((a, b) => 
        new Date(b.receptionDateTime) - new Date(a.receptionDateTime)
      );
      
      setReports(sortedReports);
    } catch (err) {
      console.error('보고서 데이터 로딩 실패:', err);
      if (err.response?.status === 401) {
        window.location.reload();
      } else {
        setError(err.message || '보고서 목록을 불러오는데 실패했습니다.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  const handleReportClick = (report) => {
    setSelectedReport(report);
    setReportModalOpen(true);
  };

  const handleReportClose = () => {
    setSelectedReport(null);
    setReportModalOpen(false);
  };

  const handleCreateReport = () => {
    setSelectedReport(null);  // 새 리포트 생성 시에는 선택된 리포트 없음
    setReportModalOpen(true);
  };

  const pendingCount = reports.filter(r => r.status === 'done').length;

  if (loading) {
    return (
      <div className="reports-panel">
        <div className="reports-header">
          <span className="reports-title">Report List</span>
          <button onClick={handleCreateReport} className="create-report-button">
            새 리포트
          </button>
        </div>
        <div className="loading">로딩 중...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="reports-panel">
        <div className="reports-header">
          <span className="reports-title">Report List</span>
          <button onClick={handleCreateReport} className="create-report-button">
            새 리포트
          </button>
        </div>
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="reports-panel">
      <div className="reports-header">
        <span className="reports-title">Report List</span>
        <button onClick={handleCreateReport} className="create-report-button">
          새 리포트
        </button>
        {pendingCount > 0 && <span className="badge">{pendingCount}</span>}
      </div>
      <ul className="reports-list">
        {reports.map((r) => (
          <li key={r.id} className="report-item" onClick={() => handleReportClick(r)}>
            <span className="report-text">{r.title}</span>
            <span className="report-time">{r.date}</span>
          </li>
        ))}
      </ul>

      {isReportModalOpen && (
        <Report
          isOpen={isReportModalOpen}
          onClose={handleReportClose}
          data={selectedReport}
          onUpdate={fetchReports}  // 리포트가 생성/수정되면 목록을 새로고침
        />
      )}
    </div>
  );
}
