import React, { useState } from "react";
import './Report.css';

export default function ReportModal({ isOpen, onClose }) {
  if (!isOpen) return null;

  // 폼 데이터 상태 관리
  const [formData, setFormData] = useState({
    receptionId: "",
    reporterName: "",
    handlerName: "",
    reporterModel: "",
    receptionDateTime: "",
    actionDetails: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();  // 폼 제출 시 페이지 새로 고침 방지
    console.log("폼 제출 데이터:", formData);
    // 데이터 처리 로직 추가 (API 요청 등)
    onClose(); // 제출 후 모달 닫기
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Report</h2>
          <button onClick={onClose} className="close-button">×</button>
        </div>

        <form className="report-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>접수번호</label>
            <input
              type="text"
              name="receptionId"
              value={formData.receptionId}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>신고자명</label>
            <input
              type="text"
              name="reporterName"
              value={formData.reporterName}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>담당자명</label>
            <input
              type="text"
              name="handlerName"
              value={formData.handlerName}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>신고자모델</label>
            <input
              type="text"
              name="reporterModel"
              value={formData.reporterModel}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>접수일자/시간</label>
            <input
              type="datetime-local"
              name="receptionDateTime"
              value={formData.receptionDateTime}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>조치내용</label>
            <textarea
              name="actionDetails"
              rows="4"
              value={formData.actionDetails}
              onChange={handleChange}
            />
          </div>

          <div className="form-actions">
            <button type="submit">저장</button>
          </div>
        </form>
      </div>
    </div>
  );
}
