import { useState } from "react";
import "./LoginModal.css";
import logo from '../assets/images/logo.png';
import axios from 'axios';

function LoginModal({ onClose, onLogin, onSwitchToRegister }) {
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async () => {
    if (!id || !pw) {
      setError("아이디와 비밀번호를 모두 입력해주세요.");
      return;
    }

    setIsLoading(true);
    setError("");
    
    try {
      const formData = new URLSearchParams();
      formData.append('username', id);
      formData.append('password', pw);

      const response = await axios.post('http://211.188.63.134/login', formData, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      });

      console.log('Login response:', response.data); // 응답 데이터 확인

      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        onLogin(id);
      } else {
        setError('서버 응답에 토큰이 없습니다.');
      }
    } catch (err) {
      console.error('Login error:', err); // 에러 상세 정보 확인
      if (err.response) {
        // 서버에서 응답이 왔지만 에러인 경우
        setError(err.response.data?.message || err.response.data?.detail || '로그인에 실패했습니다.');
      } else if (err.request) {
        // 요청은 보냈지만 응답이 없는 경우
        setError('서버에 연결할 수 없습니다.');
      } else {
        // 요청 설정 중 에러가 발생한 경우
        setError('로그인 요청 중 오류가 발생했습니다.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-modal-overlay">
      <div className="login-modal">
        <img src={logo} alt="Logo" className="login-logo" />
        <h2 className="login-title">로그인</h2>
        {error && <div className="error-message">{error}</div>}

        <input
          type="text"
          placeholder="아이디"
          value={id}
          onChange={(e) => setId(e.target.value)}
          className="login-input"
        />

        <input
          type="password"
          placeholder="비밀번호"
          value={pw}
          onChange={(e) => setPw(e.target.value)}
          className="login-input"
        />

        <button 
          className="login-button" 
          onClick={handleLogin}
          disabled={isLoading}
        >
          {isLoading ? '로그인 중...' : '로그인'}
        </button>

        <p
          className="signup-link"
          onClick={onSwitchToRegister}
        >
          회원가입
        </p>
      </div>
    </div>
  );
}

export default LoginModal;
