import React, { useState, useEffect } from 'react';
import './ArrowControls.css';

const ArrowControls = () => {
  const [activeButtons, setActiveButtons] = useState({
    up: false,
    down: false,
    left: false,
    right: false
  });

  // 마우스 이벤트 핸들러
  const handleMouseDown = (direction) => {
    setActiveButtons(prev => ({ ...prev, [direction]: true }));
  };

  const handleMouseUp = (direction) => {
    setActiveButtons(prev => ({ ...prev, [direction]: false }));
  };

  // 키보드 이벤트 핸들러
  useEffect(() => {
    const handleKeyDown = (e) => {
      switch(e.key) {
        case 'ArrowUp':
          setActiveButtons(prev => ({ ...prev, up: true }));
          break;
        case 'ArrowDown':
          setActiveButtons(prev => ({ ...prev, down: true }));
          break;
        case 'ArrowLeft':
          setActiveButtons(prev => ({ ...prev, left: true }));
          break;
        case 'ArrowRight':
          setActiveButtons(prev => ({ ...prev, right: true }));
          break;
        default:
          break;
      }
    };

    const handleKeyUp = (e) => {
      switch(e.key) {
        case 'ArrowUp':
          setActiveButtons(prev => ({ ...prev, up: false }));
          break;
        case 'ArrowDown':
          setActiveButtons(prev => ({ ...prev, down: false }));
          break;
        case 'ArrowLeft':
          setActiveButtons(prev => ({ ...prev, left: false }));
          break;
        case 'ArrowRight':
          setActiveButtons(prev => ({ ...prev, right: false }));
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return (
    <div className="arrow-controls">
      <div className="arrow-row">
        <button
          className={`arrow-button up ${activeButtons.up ? 'active' : ''}`}
          onMouseDown={() => handleMouseDown('up')}
          onMouseUp={() => handleMouseUp('up')}
          onMouseLeave={() => handleMouseUp('up')}
        >
          <div className="arrow-icon" />
        </button>
      </div>
      <div className="arrow-row">
        <button
          className={`arrow-button left ${activeButtons.left ? 'active' : ''}`}
          onMouseDown={() => handleMouseDown('left')}
          onMouseUp={() => handleMouseUp('left')}
          onMouseLeave={() => handleMouseUp('left')}
        >
          <div className="arrow-icon" />
        </button>
        <button
          className={`arrow-button down ${activeButtons.down ? 'active' : ''}`}
          onMouseDown={() => handleMouseDown('down')}
          onMouseUp={() => handleMouseUp('down')}
          onMouseLeave={() => handleMouseUp('down')}
        >
          <div className="arrow-icon" />
        </button>
        <button
          className={`arrow-button right ${activeButtons.right ? 'active' : ''}`}
          onMouseDown={() => handleMouseDown('right')}
          onMouseUp={() => handleMouseUp('right')}
          onMouseLeave={() => handleMouseUp('right')}
        >
          <div className="arrow-icon" />
        </button>
      </div>
    </div>
  );
};

export default ArrowControls;
