import React from 'react';
import './ReportsPanel.css';

/** 더미 데이터: props로 안 넘겨주면 이걸 씁니다 */
const dummyReports = [
  { title: '주간 매출 보고서',       date: '2025-05-10', status: 'pending' },
  { title: '월간 사용자 통계',       date: '2025-05-01', status: 'done'    },
  { title: '분기별 성과 분석',       date: '2025-04-01', status: 'pending' },
  { title: '고객 만족도 조사 결과', date: '2025-03-25', status: 'done'    },
  { title: '신규 기능 릴리즈 노트', date: '2025-03-15', status: 'pending' },
  { title: '연간 예산 검토',         date: '2025-01-30', status: 'done'    },
  { title: '보안 점검 리포트',       date: '2024-12-20', status: 'pending' },
];

/**
 * ReportsPanel
 * @param {Array<{ title: string, date: string, status?: string }>} reports
 */
export default function ReportsPanel({ reports }) {
  const list = reports && reports.length > 0 ? reports : dummyReports;
  const pendingCount = list.filter(r => r.status === 'pending').length;

  return (
    <div className="reports-panel">
      <div className="reports-header">
        <span className="reports-title">리포트</span>
        {pendingCount > 0 && <span className="badge">{pendingCount}</span>}
      </div>
      <ul className="reports-list">
        {list.map((r, i) => (
          <li key={i} className="report-item">
            <span className="report-text">{r.title}</span>
            <span className="report-time">{r.date}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}
