import { useState } from "react";
import "./LoginModal.css";
import logo from '../assets/images/logo.png';
import axios from 'axios';

function LoginModal({ onClose, onLogin, onSwitchToRegister }) {
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault(); // 폼 제출 기본 동작 방지
    
    if (!id || !pw) {
      setError("아이디와 비밀번호를 모두 입력해주세요.");
      return;
    }

    setIsLoading(true);
    setError("");
    
    try {
      const response = await axios.post('http://211.188.63.134/login', {
        username: id,
        password: pw
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log('Login response:', response.data);

      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        onLogin(id);
      } else {
        setError('서버 응답에 토큰이 없습니다.');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.response) {
        setError(err.response.data?.message || err.response.data?.detail || '로그인에 실패했습니다.');
      } else if (err.request) {
        setError('서버에 연결할 수 없습니다.');
      } else {
        setError('로그인 요청 중 오류가 발생했습니다.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-modal-overlay">
      <div className="login-modal">
        <img src={logo} alt="Logo" className="login-logo" />
        <h2 className="login-title">로그인</h2>
        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="아이디"
            value={id}
            onChange={(e) => setId(e.target.value)}
            className="login-input"
            disabled={isLoading}
          />

          <input
            type="password"
            placeholder="비밀번호"
            value={pw}
            onChange={(e) => setPw(e.target.value)}
            className="login-input"
            disabled={isLoading}
          />

          <button 
            type="submit"
            className="login-button" 
            disabled={isLoading}
          >
            {isLoading ? '로그인 중...' : '로그인'}
          </button>
        </form>

        <p
          className="signup-link"
          onClick={onSwitchToRegister}
          style={{ pointerEvents: isLoading ? 'none' : 'auto' }}
        >
          회원가입
        </p>
      </div>
    </div>
  );
}

export default LoginModal;
