import React, { useState, useEffect, useRef } from 'react';
import { useWebSocket } from '../contexts/WebSocketContext';
import './StreamPanel.css';

const StreamPanel = () => {
  const imageRef = useRef(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const { ws, isConnected, connectedCustomer } = useWebSocket();

  // Customer information state
  const [recognitionPerson, setRecognitionPerson] = useState('담당자명: ');
  const [recognitionCar, setRecognitionCar] = useState('차량모델: ');
  const [recognitionTime, setRecognitionTime] = useState('');

  // Update customer information when connected customer changes
  useEffect(() => {
    if (connectedCustomer) {
      setRecognitionPerson(`담당자명: ${connectedCustomer.name}`);
      setRecognitionCar(`차량모델: ${connectedCustomer.model}`);
      setRecognitionTime(connectedCustomer.timestamp);
      setIsStreaming(true);
    } else {
      setRecognitionPerson('담당자명: ');
      setRecognitionCar('차량모델: ');
      setRecognitionTime('');
      setIsStreaming(false);
    }
  }, [connectedCustomer]);

  // WebSocket message handling
  useEffect(() => {
    if (!ws || !connectedCustomer) {
      setIsStreaming(false);
      return;
    }

    const handleMessage = (event) => {
      const data = JSON.parse(event.data);

      // Handle camera feed
      if (data.topic === '/camera/image_raw/compressed') {
        if (imageRef.current) {
          const imageData = `data:image/jpeg;base64,${data.msg.data}`;
          imageRef.current.src = imageData;
        }
      }
    };

    ws.addEventListener('message', handleMessage);

    return () => {
      ws.removeEventListener('message', handleMessage);
    };
  }, [ws, connectedCustomer]);

  return (
    <div className="stream-container">
      <div className="stream-panel">
        <h2 className="panel-title">Webcam</h2>
        <div className="cameras-container">
          {isStreaming && connectedCustomer ? (
            <div className="camera-feed">
              <img
                ref={imageRef}
                alt="Camera Feed"
                onError={() => setIsStreaming(false)}
                className="camera-image"
              />
            </div>
          ) : (
            <div className="camera-placeholder">
              <p>고객 연결 대기 중입니다...</p>
            </div>
          )}
        </div>
      </div>
      <div className="divider"></div>
      <div className="recognition-info">
        <div className="recognition-person">{recognitionPerson}</div>
        <div className="recognition-car">{recognitionCar}</div>
        <div className="recognition-time">{recognitionTime}</div>
      </div>
    </div>
  );
};

export default StreamPanel;
