import React, { useEffect, useRef, useState } from 'react';
import './MapPanel.css';

const MapPanel = () => {
  const mapRef = useRef(null);
  const [teleopMode, setTeleopMode] = useState(false);

  useEffect(() => {
    const initMap = () => {
      const container = mapRef.current;
      const options = {
        center: new window.kakao.maps.LatLng(37.5665, 126.9780),
        level: 3
      };

      const map = new window.kakao.maps.Map(container, options);

      const marker = new window.kakao.maps.Marker({
        position: options.center
      });
      marker.setMap(map);

      const zoomControl = new window.kakao.maps.ZoomControl();
      map.addControl(zoomControl, window.kakao.maps.ControlPosition.RIGHT);

      const mapTypeControl = new window.kakao.maps.MapTypeControl();
      map.addControl(mapTypeControl, window.kakao.maps.ControlPosition.TOPRIGHT);
    };

    const script = document.createElement('script');
    script.src = '//dapi.kakao.com/v2/maps/sdk.js?appkey=09353682caee403f91118b946610d963&autoload=false';
    script.async = true;

    script.onload = () => {
      window.kakao.maps.load(() => {
        initMap();
      });
    };

    if (!document.querySelector('script[src*="dapi.kakao.com"]')) {
      document.head.appendChild(script);
    } else if (window.kakao && window.kakao.maps) {
      window.kakao.maps.load(() => {
        initMap();
      });
    }

    return () => {
      // cleanup if needed
    };
  }, []);

  const handleArrowClick = (direction) => {
    console.log('Arrow clicked:', direction);
    // 여기에 화살표 버튼 클릭 시 실행할 로직을 추가하세요
  };

  return (
    <>
      <div className="teleop-panel">
        <h2 className="panel-title">Teleop Control</h2>
        <button 
          className={`teleop-toggle ${teleopMode ? 'active' : ''}`}
          onClick={() => setTeleopMode(!teleopMode)}
        >
          Teleop Mode {teleopMode ? 'ON' : 'OFF'}
        </button>
        {teleopMode && (
          <div className="arrow-controls">
            <div className="arrow-row">
              <button className="arrow-button" onClick={() => handleArrowClick('up')}>↑</button>
            </div>
            <div className="arrow-row">
              <button className="arrow-button" onClick={() => handleArrowClick('left')}>←</button>
              <button className="arrow-button" onClick={() => handleArrowClick('down')}>↓</button>
              <button className="arrow-button" onClick={() => handleArrowClick('right')}>→</button>
            </div>
          </div>
        )}
      </div>
      <div className="map-panel">
        <h2 className="panel-title">Map</h2>
        <div ref={mapRef} className="map-view" />
      </div>
    </>
  );
};

export default MapPanel;
