import React, { useEffect, useState } from 'react';
import { useWebSocket } from '../contexts/WebSocketContext';
import './SpeedPanel.css';
import { FaTachometerAlt } from 'react-icons/fa';

const SpeedPanel = () => {
  const [speed, setSpeed] = useState(0.0);
  const { ws, connectedCustomer } = useWebSocket();

  useEffect(() => {
    if (!ws || !connectedCustomer) return;

    const handleMessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.topic === '/cmd_vel') {
        // 선속도와 각속도의 절대값 중 큰 값을 표시
        const linearSpeed = Math.abs(data.msg.linear.x);
        const angularSpeed = Math.abs(data.msg.angular.z);
        const currentSpeed = Math.max(linearSpeed, angularSpeed);
        setSpeed(currentSpeed.toFixed(2));
      }
    };

    ws.addEventListener('message', handleMessage);

    return () => {
      ws.removeEventListener('message', handleMessage);
    };
  }, [ws, connectedCustomer]);

  // 고객이 연결되어 있지 않으면 null을 반환하여 컴포넌트를 렌더링하지 않음
  if (!connectedCustomer) {
    return null;
  }

  return (
    <div className="speed-container">
      <div className="speed-panel">
        <div className="speed-content">
          <div className="speed-info">
            <div className="speed-label">Speed</div>
            <div className="speed-value">{speed}</div>
          </div>
          <div className="speed-icon">
            <FaTachometerAlt />
          </div>
        </div>
      </div>
    </div>
  );
};

export default SpeedPanel;
