import React from 'react';
import './ReportsPanel.css';

/**
 * ReportsPanel
 * @param {Array<{ title: string, date: string, status?: string }>} reports
 * @param {function} onViewAll - 모두 보기 버튼 클릭 핸들러
 */
export default function ReportsPanel({ reports = [], onViewAll = () => {} }) {
  const pendingCount = reports.filter(r => r.status === 'pending').length;

  return (
    <div className="card reports-panel">
      <div className="reports-header">
        <span className="reports-title">리포트</span>
        {pendingCount > 0 && <span className="badge">{pendingCount}</span>}
      </div>
      <ul className="reports-list">
        {reports.slice(0, 5).map((r, i) => (
          <li key={i} className="report-item">
            <span className={`icon ${r.status || ''}`}></span>
            <span className="report-text">{r.title}</span>
            <span className="report-time">{r.date}</span>
          </li>
        ))}
      </ul>
      <button className="btn-secondary" onClick={onViewAll}>
        모두 보기
      </button>
    </div>
  );
}
