import React, { useState, useEffect } from 'react';
import './PlatformControlPanel.css';

const PlatformControlPanel = () => {
  const [teleopMode, setTeleopMode] = useState(false);
  const [isKeyPressed, setIsKeyPressed] = useState({
    up: false,
    down: false,
    left: false,
    right: false
  });

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!teleopMode) return;

      switch (e.key) {
        case 'ArrowUp':
          e.preventDefault();
          setIsKeyPressed(prev => ({ ...prev, up: true }));
          // TODO: Send platform forward command
          break;
        case 'ArrowDown':
          e.preventDefault();
          setIsKeyPressed(prev => ({ ...prev, down: true }));
          // TODO: Send platform backward command
          break;
        case 'ArrowLeft':
          e.preventDefault();
          setIsKeyPressed(prev => ({ ...prev, left: true }));
          // TODO: Send platform left turn command
          break;
        case 'ArrowRight':
          e.preventDefault();
          setIsKeyPressed(prev => ({ ...prev, right: true }));
          // TODO: Send platform right turn command
          break;
        default:
          break;
      }
    };

    const handleKeyUp = (e) => {
      if (!teleopMode) return;

      switch (e.key) {
        case 'ArrowUp':
          setIsKeyPressed(prev => ({ ...prev, up: false }));
          // TODO: Stop forward movement
          break;
        case 'ArrowDown':
          setIsKeyPressed(prev => ({ ...prev, down: false }));
          // TODO: Stop backward movement
          break;
        case 'ArrowLeft':
          setIsKeyPressed(prev => ({ ...prev, left: false }));
          // TODO: Stop left turn
          break;
        case 'ArrowRight':
          setIsKeyPressed(prev => ({ ...prev, right: false }));
          // TODO: Stop right turn
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [teleopMode]);

  const handleEmergencyStop = () => {
    // TODO: Implement emergency stop logic
    setTeleopMode(false);
    setIsKeyPressed({
      up: false,
      down: false,
      left: false,
      right: false
    });
  };

  return (
    <div className="platform-control-panel">
      <h2 className="panel-title">Platform Control</h2>
      <div className="control-container">
        <div className="mode-controls">
          <button
            className={`teleop-toggle ${teleopMode ? 'active' : ''}`}
            onClick={() => setTeleopMode(!teleopMode)}
          >
            Teleop Mode: {teleopMode ? 'ON' : 'OFF'}
          </button>
          <button
            className="emergency-stop"
            onClick={handleEmergencyStop}
          >
            E-STOP
          </button>
        </div>

        {teleopMode && (
          <div className="arrow-controls">
            <div className="arrow-row">
              <button
                className={`arrow-key ${isKeyPressed.up ? 'pressed' : ''}`}
                disabled={!teleopMode}
              >
                ↑
              </button>
            </div>
            <div className="arrow-row">
              <button
                className={`arrow-key ${isKeyPressed.left ? 'pressed' : ''}`}
                disabled={!teleopMode}
              >
                ←
              </button>
              <button
                className={`arrow-key ${isKeyPressed.down ? 'pressed' : ''}`}
                disabled={!teleopMode}
              >
                ↓
              </button>
              <button
                className={`arrow-key ${isKeyPressed.right ? 'pressed' : ''}`}
                disabled={!teleopMode}
              >
                →
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PlatformControlPanel;
