import React, { useEffect, useRef } from 'react';
import './MapPanel.css';

const MapPanel = () => {
  const mapContainer = useRef(null);

  useEffect(() => {
    const loadKakaoMap = () => {
      if (window.kakao && window.kakao.maps) {
        // 지도 초기 설정
        const options = {
          center: new window.kakao.maps.LatLng(37.5665, 126.9780), // 서울 시청 좌표
          level: 3 // 지도 확대 레벨
        };

        // 지도 생성
        const map = new window.kakao.maps.Map(mapContainer.current, options);

        // 마커 생성
        const markerPosition = new window.kakao.maps.LatLng(37.5665, 126.9780);
        const marker = new window.kakao.maps.Marker({
          position: markerPosition
        });

        // 마커를 지도에 표시
        marker.setMap(map);

        // 지도 컨트롤러 추가
        const zoomControl = new window.kakao.maps.ZoomControl();
        map.addControl(zoomControl, window.kakao.maps.ControlPosition.RIGHT);

        const mapTypeControl = new window.kakao.maps.MapTypeControl();
        map.addControl(mapTypeControl, window.kakao.maps.ControlPosition.TOPRIGHT);
      }
    };

    // 카카오맵 스크립트 로드 체크
    if (window.kakao && window.kakao.maps) {
      loadKakaoMap();
    } else {
      const script = document.createElement('script');
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=c73081b0d3d990b7885bae58963fb081&autoload=false`;
      script.async = true;

      script.onload = () => {
        window.kakao.maps.load(loadKakaoMap);
      };

      document.head.appendChild(script);
    }

    // cleanup function
    return () => {
      const script = document.querySelector('script[src*="dapi.kakao.com/v2/maps/sdk.js"]');
      if (script) {
        script.remove();
      }
    };
  }, []);

  return (
    <div className="map-panel">
      <div 
        ref={mapContainer} 
        style={{ 
          width: '100%', 
          height: '100%' 
        }}
      />
    </div>
  );
};

export default MapPanel;
